import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { Row, Col, Spin, Image, message } from "antd";
import StyledButton from "../../components/Button";
import { StarFilled } from "@ant-design/icons";
import "../../basic.css";

const imageFallback =
	"data:image/png;base64,";

const MovieDetailPage = (props) => {
	const [movie, setMovie] = useState({});
	const [loading, setLoading] = useState(true);
	const movieId = props.match.params.id;

	const success = () => {
		message.success("삭제 성공");
	};

	const error = () => {
		message.error("삭제 실패");
	};

	useEffect(() => {
		const fetchData = async () => {
			try {
				const res = await axios.get(
					`https://limitless-sierra-67996.herokuapp.com/v1/movies/${movieId}`
				);
				setMovie(res.data);
				setLoading(false);
			} catch (err) {
				console.log(err);
			}
		};
		fetchData();
	}, [movieId]);

	const deleteData = async () => {
		try {
			const req = await axios.delete(
				`https://limitless-sierra-67996.herokuapp.com/v1/movies/${movieId}`
			);
			console.log(req);
			if (req.status === 204) {
				success();
			} else error();
		} catch (err) {
			console.log(err);
		}
	};

	return (
		<>
			{loading ? (
				<Spin size="large" />
			) : (
				<>
					<Row justify="end" gutter={8}>
						<Col className="margin-bottom-small">
							<StyledButton>
								<Link to={`/detail/${movieId}/edit`}>영화 수정하기</Link>
							</StyledButton>
						</Col>
						<Col>
							<StyledButton onClick={deleteData}>영화 삭제하기</StyledButton>
						</Col>
					</Row>
					<Row gutter={32}>
						<Col span={8} className="border">
							<Image
								width={"100%"}
								src="error"
								fallback={imageFallback}
								className="margin-bottom-medium"
							/>
							<div>
								<strong>감독</strong>
								<p>{movie.director}</p>
							</div>
							<div>
								<strong>장르</strong>
								<p>
									{movie.categories.map((category, index) => (
										<span key={index}>{category} </span>
									))}
								</p>
							</div>
						</Col>
						<Col span={16}>
							<Row className="border">
								<h2>
									{`${movie.title}(${movie.year})`}
									<span>
										<StarFilled />
										{movie.rating}
									</span>
								</h2>
							</Row>
							<Row className="border">{movie.summary}</Row>
						</Col>
					</Row>
				</>
			)}
		</>
	);
};

export default MovieDetailPage;
